import React, { useState } from 'react'
import { Container, Button, Form } from 'react-bootstrap';

const Addnew = () => {
    const[inpval,setInp]=useState({
        title:"",
        desc:""
    })

    const setdata=(e)=>{
        console.log(e.target.value);
        const{name,value}=e.target;
        setInp((preval)=>{
            return{
                ...preval,[name]:value
            }
        })
    }


    const onSave =async (e) => {
        e.preventDefault();
        const { title, description } = inpval;
        const res = await fetch("/register", {
            method: "POST",
            Headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                title, description
            })
        })
const data=await res.json();
console.log(data)
    }
    return (
        <Container className='mt-5' style={{ width: 700 }}>
            <div style={{ justifyContent: 'center', textAlign: 'center' }} className='mt-5 mb-5'>
                <h1>Add Your Tasks</h1>
            </div>
            <Form onSubmit={onSave}>
                <Form.Group className="mb-5" controlId="formBasicEmail">
                    <Form.Control
                        type="text"
                        placeholder="Title"
                        name="title"
                        onChange={setdata}
                        value={inpval.title}
                        required
                    />
                </Form.Group>

                <Form.Group className="mb-5" >
                    <textarea
                        className="form-control"
                        rows="5"
                        placeholder="Description"
                        onChange={setdata}
                        name="desc"
                        value={inpval.desc}
                        required >
                    </textarea>
                </Form.Group>
                <div className="text-center">
                    <Button variant="primary" type="submit" className="mb-5 form-control">
                        Save
                    </Button>
                </div>
            </Form>
        </Container>
    )
}

export default Addnew
