import React, { useEffect, useState } from 'react'
import { Container, Row, Col, Button, } from 'react-bootstrap';
import Form from 'react-bootstrap/Form';
import { Link } from 'react-router-dom'
import InputGroup from 'react-bootstrap/InputGroup';

const Home = () => {
    const [getuserdata, setuserdata] = useState([])
    console.log(getuserdata)


    const getdata = async (e) => {

        const res = await fetch("http://localhost:8000/getdata", {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
        })
        const data = await res.json();
        console.log(data);

        if (res.status === 404 || !data) {
            console.log('error')
        }
        else {
            setuserdata(data)
            console.log('data got')
        }
    }


    const onDlt = (index) => {
        alert('item deleted')
    }
    const onAdd = () => {
        alert('item added')
    }

    useEffect(() => {
        getdata()
    }, [])

    return (

        <Container className='mt-5 pt-5' style={{ width: 1100 }}>
            <InputGroup className="mb-5">
                <Form.Control
                    placeholder="Search Here" />
                <InputGroup.Text >Search</InputGroup.Text>
            </InputGroup>
            <Link to="/Addnew" className="btn btn-primary">Add New</Link>
            <div>
                {/* <ul>
                    {
                        getuserdata.map((Element, id) =>
                            <li key={id}><Button variant='' onClick={() => onDlt(id)}>{Element.title}</Button></li>
                        )
                    }
                </ul> */}
                <table class="table">
                    <thead class="thead-light">
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Title</th>
                            <th scope="col">Edit</th>
                            <th scope="col">Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            getuserdata.map((Element, id) => {
                                return (
                                    <>
                                        <tr>
                                            <th scope="row">{id+1}</th>
                                            <td>{Element.title}</td>
                                            <td><Button variant='info'>Edit</Button></td>
                                            <td>@mdo</td>
                                        </tr>
                                    </>
                                )
                            })
                        }

                    </tbody>
                </table>
            </div>
        </Container>
    )
}

export default Home
