import React, { useState } from 'react'
import { Container, Row, Col,Button } from 'react-bootstrap';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import { addNewTodo } from './redux/action';

const Home = () => {
    const [item,setItem]=useState('')
    const [List,setList]=useState([])
  
  const onAdd=()=>{
    const newArr=[...List]
    newArr.push(item)
    setList(newArr)
    setItem('')
  }
  const onDlt=(index)=>{
   alert('item deleted')
  }
    return (
        <Container className='mt-5 pt-5' style={{ width: 1100 }}>
             <InputGroup className="mb-5">
                        <Form.Control
                            placeholder="Search Here" />
                        <InputGroup.Text >Search</InputGroup.Text>
                    </InputGroup>
            <Row>
                <Col sm={6}>
                    <InputGroup className="mb-3">
                        <Form.Control
                            placeholder="Title " 
                            onChange={(e)=>setItem(e.target.value)}
                            value={item}
                            />
                        <InputGroup.Text onClick={onAdd}>Add</InputGroup.Text>
                    </InputGroup>
                </Col>
                <Col sm={6}>
                    <InputGroup className="mb-3">
                    <textarea class="form-control" rows="3" placeholder="Description" name="message" required ></textarea>
                    </InputGroup>
                </Col>
            </Row>

        <div>
          <ul>
             {
              List.map((Element,index)=>
              <li key={index}><Button variant='' onClick={()=>onDlt(index)}>{Element}</Button></li>

              )
            }
          </ul>
        </div>
        </Container>
    )
}

export default Home
