import React, { useState } from 'react'
import { Container, Row, Col, Button, } from 'react-bootstrap';
import InputGroup from 'react-bootstrap/InputGroup';
import Form from 'react-bootstrap/Form';


const Addnew = () => {
    const [title, setTitle] = useState('')
    const [desc, setDesc] = useState('')

    const onSave = () => {
        alert('saved')
    }
    return (<form >

        <Container className='mt-5' style={{ width: 700 }}>
        <div style={{justifyContent:'center',textAlign:'center'}} className='mt-5 mb-5'>
        <h1>Add Your Tasks</h1>
            </div>
            <InputGroup className="mb-3">
                <Form.Control
                    placeholder="Title"
                    onChange={(e) => setItem(e.target.value)}
                    value={title}
                />
            </InputGroup>
            <InputGroup className="mb-3">
                <textarea class="form-control" rows="5" placeholder="Description" name="description" required ></textarea>
            </InputGroup>
            <div style={{justifyContent:'center',alignItems:'center'}}>
            <Button variant='primary' onClick={onSave}>Save</Button>
            </div>
        </Container>

    </form>
    )
}

export default Addnew
